mapper.select:
SELECT lh.changed_at AS changedAt,
CASE WHEN lh.before_grade IS NULL THEN '가입' WHEN lh.before_grade != lh.after_grade THEN '등급 변경' ELSE '변경' END AS changeType,
CASE WHEN lh.before_grade IS NULL THEN lh.after_grade ELSE CONCAT(lh.before_grade, ' => ', lh.after_grade) END AS content,
CASE WHEN lh.change_source = 'AUTO' THEN 'SYSTEM' ELSE 'MANUAL' END AS changeSource,
CASE WHEN lh.change_source = 'AUTO' THEN NULL ELSE lh.employee_code END AS changedByEmployeeCode
FROM loyalty_history lh JOIN loyalty l ON l.loyalty_code = lh.loyalty_code AND l.hotel_group_code = #{hotelGroupCode} AND l.customer_code = #{customerCode}
WHERE lh.changed_at >= #{from} AND lh.changed_at <= #{to}
ORDER BY lh.changed_at DESC LIMIT #{page.size} OFFSET #{page.offset}

SELECT COUNT(1)
FROM loyalty_history lh
JOIN loyalty l
ON l.loyalty_code = lh.loyalty_code
AND l.hotel_group_code = #{hotelGroupCode}
AND l.customer_code = #{customerCode}
WHERE lh.changed_at  >=  #{from}
AND lh.changed_at  <=  #{to}
