mapper.select:
SELECT COUNT(*)
FROM reservation r
JOIN property p ON r.property_code = p.property_code
WHERE p.hotel_group_code = #{hotelGroupCode}
AND r.checkin_date  <=  #{today}
AND r.checkout_date  >=  #{today}

SELECT COUNT(*)
FROM reservation r
JOIN property p ON r.property_code = p.property_code
WHERE p.hotel_group_code = #{hotelGroupCode}
AND r.checkin_date = #{today}
AND r.reservation_status = 'RESERVED'

SELECT COUNT(*)
FROM reservation r
JOIN property p ON r.property_code = p.property_code
WHERE p.hotel_group_code = #{hotelGroupCode}
AND r.checkout_date = #{today}
AND r.reservation_status = 'RESERVED'
