mapper.select:
SELECT 'CHECKIN_PLANNED' AS operationStatus, COUNT(*) AS cnt FROM reservation r INNER JOIN property p ON r.property_code = p.property_code AND p.hotel_group_code = #{hotelGroupCode} LEFT JOIN stay s ON r.reservation_code = s.reservation_code WHERE r.reservation_status = 'RESERVED' AND r.canceled_at IS NULL AND r.checkin_date = #{today} AND s.stay_code IS NULL
AND r.property_code = #{propertyCode}
UNION ALL
SELECT 'STAYING' AS operationStatus, COUNT(*) AS cnt FROM reservation r INNER JOIN property p ON r.property_code = p.property_code AND p.hotel_group_code = #{hotelGroupCode} LEFT JOIN stay s ON r.reservation_code = s.reservation_code WHERE r.reservation_status = 'RESERVED' AND r.canceled_at IS NULL AND s.actual_checkin_at IS NOT NULL AND s.actual_checkout_at IS NULL AND #{today} BETWEEN r.checkin_date AND r.checkout_date
AND r.property_code = #{propertyCode}
UNION ALL
SELECT 'CHECKOUT_PLANNED' AS operationStatus, COUNT(*) AS cnt FROM reservation r INNER JOIN property p ON r.property_code = p.property_code AND p.hotel_group_code = #{hotelGroupCode} LEFT JOIN stay s ON r.reservation_code = s.reservation_code WHERE r.reservation_status = 'RESERVED' AND r.canceled_at IS NULL AND r.checkout_date = #{today} AND s.actual_checkin_at IS NOT NULL AND s.actual_checkout_at IS NULL
AND r.property_code = #{propertyCode}
