mapper.select:
SELECT DISTINCT c.customer_code AS customerCode, c.customer_name_enc AS customerNameEnc, cc.contact_value_enc AS phoneEnc, c.dek_enc AS dekEnc FROM stay s INNER JOIN reservation r ON s.reservation_code = r.reservation_code INNER JOIN property p ON r.property_code = p.property_code INNER JOIN customer c ON s.customer_code = c.customer_code LEFT JOIN customer_contact cc ON cc.customer_code = c.customer_code AND cc.contact_type = 'PHONE' AND cc.is_primary = TRUE WHERE p.hotel_group_code = #{hotelGroupCode}
AND ( CAST(c.customer_code AS CHAR) LIKE CONCAT('%', #{keyword}, '%') OR c.customer_name_hash LIKE CONCAT('%', #{keyword}, '%') OR EXISTS ( SELECT 1 FROM customer_contact cx WHERE cx.customer_code = c.customer_code AND cx.contact_type = 'PHONE' AND cx.contact_value_hash LIKE CONCAT('%', #{keyword}, '%') ) )
ORDER BY c.customer_code DESC LIMIT 100
